                        $.ajax({
                            type: ""GET"",
                            url: `/Book/Delete/${bookId}`,
                            success: function (data) {
                                if (data == 1) {
                                    alert(""Silme işlemi başarılı."");
                                    window.location.href = ""/"";
                                }
